let Util = require("./Util");


module.exports = {

	getReleasesUrl(midi) {
		return `rel${midi - 20}.[mp3|ogg]`;
	},

	getHarmonicsUrl(midi) {
		return `harmL${Util.midiToNote(midi).replace('#', 's')}.[mp3|ogg]`;
	},

	getNotesUrl(midi, vel) {
		return `${Util.midiToNote(midi).replace('#', 's')}v${vel}.[mp3|ogg]`;
	}
};
