const examPage = {
	switch: async reload => {
		console.log(...log(`examPage.switch(${reload})`));
		EStore.setLastPage('exam');
		if (reload)
			return reloadPage();

		await asx.$fadeOut($MainContent, 100);
		$MainContent.empty();
		require("pyano_local_modules/sidebar").to_exam();
		safeSwitchCss("templates/css/exam.css");
		let $bigMessage = $('<div id="big_message">')
			.text('Drop a file');
		let $dropArea = $('<div id="drop_area">')
			.on({
				'dragover dragenter': e => {
					e.preventDefault();
					e.stopPropagation();
				}, 'drop': async e => await onDrop(e)
			});

		$MainContent.append($bigMessage, $dropArea);
		await asx.$fadeIn($MainContent, 300);
	}
};

module.exports = examPage;
