const $ = require('jquery');
let {
	$fadeIn,
	$fadeInMany,
	$fadeOut,
	$fadeOutMany,
	concurrent,
	Messages,
	strong,
	wait,
} = require("pyano_local_modules/util");
let { EStore, Python } = require("pyano_local_modules/ext_libs");
const Svg = require("./Svg");
const fs = require("fs");

const { $Sidebar } = require("pyano_local_modules/document");


const log = s => [`%c${s}`, 'color:#9876aa'];
const logmod = s => [`Gui.%c${s}`, 'color:#9876aa'];

const F = 1;

class Video {
	/**@param {jQuery} $video*/
	constructor($video) {
		this.$element = $video;
		this.firstOnset = null;
	}

	/**@param {Truth} truth*/
	async init(truth) {
		let vidsrcfile = `file:///${truth.mp4.path}`;
		let src = `<source src="${vidsrcfile}" type="video/mp4">`;
		this.$element.append(src);
		let data = await fs.readFileSync(truth.onsets.path);
		/**@type {{onsets:String[],first_onset_index:number}}*/
		data = JSON.parse(data);
		console.log('Video.data from onsets.json', { data });
		this.firstOnset = float(data.onsets[data.first_onset_index]);
		this.lastOnset = float(data.onsets.last());

		const events = [
			'canplay',  // not enough data has been loaded to play the media up to its end
			'canplaythrough', // can play the media up to its end
			'emptied',
			'ended', // reached end
			'loadeddata', // first frame of media has finished loading
			'loadedmetadata',
			'play', // Playback has begun
			'playing', // Playback is ready to start after having been paused or delayed due to lack of data.
			'stalled', // trying to fetch media data, but is not coming
			'suspend', // Media data loading has been suspended.
			'waiting', // stopped because of a temporary lack of data
		];
		for (let ev of events) {
			const evFired = `${ev}Fired`;
			this[evFired] = false;
			this.vidElm[`on${ev}`] = () => this[evFired] = true;
		}
		// this.vidElm.addEventListener('mousedown', e => {
		// 	console.log('%cvideo mousedown', 'color:#de7d36', { isTrusted: e.isTrusted });
		// 	this.vidElm.play();
		// }, { once: true });
		// this.vidElm.onmousedown = e => {
		// 	console.log('%cvideo mousedown', 'color:#de7d36', { isTrusted: e.isTrusted });
		// 	this.vidElm.play();
		// };

	}

	_promiseMousedown() {
		console.log(...small('_promiseMousedown() started'));
		return new Promise(resolve =>
			this.vidElm.addEventListener('mousedown', e => {
				console.log('%cvideo mousedown', 'color:#de7d36', { isTrusted: e.isTrusted });
				console.log(...small('_promiseMousedown() resolving'));
				resolve();
			}, { once: true }));
	}

	_promisePlaying() { // TODO: This is not really working
		console.log(...small('_promisePlaying() started'));
		return new Promise(resolve =>
			this.vidElm.onplaying = () => {
				console.log(...small('_promisePlaying() resolving'));
				this.vidElm.onplaying = null;
				resolve();
			});
	}

	_promiseEnded() { // on err
		console.log(...small('_promiseEnded() started'));
		return new Promise(resolve => this.vidElm.onended = () => {
			console.log(...small('_promiseEnded() resolving'));
			this.vidElm.onended = null;
			resolve();
		});
	}

	setCurrentTimeToFirstOnset() {
		console.log(...small('setCurrentTimeToFirstOnset()'));
		this.vidElm.currentTime = this.firstOnset - 0.1;
	}


	_promiseReady() {
		console.log('_promiseReady() started');
		return new Promise(resolve => this.vidElm.onloadeddata = () => {
			console.log('_promiseReady() resolving');
			resolve();
		});
	}


	_promiseFadeIn() {
		console.log(...small('_promiseFadeIn()'));
		if (this.vidElm.volume != 0)
			console.warn('_promiseFadeIn volume not 0', this.vidElm.volume);
		return new Promise(resolve => this.$element.animate({ volume: 1 }, 50, resolve));
	}

	_promiseFadeOut() {
		console.log(...small('_promiseFadeOut()'));
		if (this.vidElm.volume != 1)
			console.warn('_promiseFadeIn volume not 1', this.vidElm.volume);
		return new Promise(resolve => this.$element.animate({ volume: 0 }, 150, resolve));
	}

	_logEvents() {
		console.group(...small('_logEvents()'));
		for (let [evprop, val] of enumerate(this))
			if (evprop.endsWith('Fired')) {
				const strings = small(evprop.upTo('Fired'));
				if (val)
					console.log(...strings, val);
				else
					console.log(...strings);

				this.evprop = false;
			}
		console.groupEnd();
	}

	/**@return {HTMLMediaElement}*/
	get vidElm() {
		return this.$element[0];
	}

	async _getWaitValue(numOfNotes) {
		let waitValue;
		let logData = {};
		if (numOfNotes == null) {
			if (this.lastOnset < this.vidElm.duration) {
				let diff = this.vidElm.duration - this.lastOnset;
				diff = min(1.5, diff);
				logData = { ...logData, diff };
				waitValue = 1000 * (this.lastOnset + diff - this.firstOnset);
			} else {
				waitValue = 1000 * (this.vidElm.duration - this.firstOnset);
			}

			logData = {
				...logData,
				'this.firstOnset': this.firstOnset,
				'this.lastOnset': this.lastOnset,
				'this.vidElm.duration': this.vidElm.duration,

			};
		} else {
			const options = { args: [EStore.truthFilePath('txt')], mode: "json" };
			let messages = await Python.runAsync('InsideTest/normalize_txt_file.py', options);
			/**@type {Messages}*/
			messages = new Messages(messages);
			let tdeltas = messages.time_deltas(numOfNotes);

			waitValue = sum(tdeltas) * 1000;

			logData = { ...logData, tdeltas, 'only relevant messages': messages._msgs.slice(0, tdeltas.length), messages };


		}
		console.log('%c_getWaitValue() -> returning waitValue, before try/catch', 'font-size: 900', {
			waitValue,
			'this.vidElm.currentTime': this.vidElm.currentTime,
			...logData
		});
		return waitValue;
	}

	/**@param {Number?} numOfNotes
	 @return {Promise<void>}*/
	async play(numOfNotes = null) {
		console.group(`Video.play(numOfNotes = `, numOfNotes, ')');
		// this._logEvents();
		const { canplaythroughFired, loadeddataFired } = this;
		if (!canplaythroughFired || !loadeddataFired) {
			console.log('\t!canplaythroughFired || !loadeddataFired, calling load()');
			this.vidElm.load();
			await this._promiseReady();
		}
		// this.setCurrentTimeToFirstOnset();
		// await wait(1000);
		this.vidElm.volume = 0;
		let waitValue = await this._getWaitValue(numOfNotes);


		try {
			console.log(...log('trying...'));
			await concurrent(this._promiseFadeIn(), this.vidElm.play(), wait(waitValue));

			await this._promiseFadeOut();
			await this.vidElm.pause();
			// this.setCurrentTimeToFirstOnset();
			console.log(...log('succeeded...'));
		} catch (e) {
			console.log(...log('catching...'));
			if (!e.message.startsWith("play() failed because the user didn't interact with the document first.")) {
				Alert.small.error("Can't play", e.message);
				console.groupEnd();
				throw e;
			}
			this.$element.css('z-index', 2000);
			let { remote } = require("electron");
			console.log('%cFaking mousedown before', 'color:#de7d36');
			remote.getCurrentWindow().webContents.sendInputEvent({
				type: 'mouseDown',
				x: 1000,
				y: 500,
				button: 'left',
			});
			// await this._promiseMousedown();
			console.log('%cFaking mousedown after (waiting 100ms)', 'color:#de7d36');
			await wait(100);
			console.log('%cFaking mousedown done waiting, play()', 'color:#de7d36');
			await this.vidElm.play();


			// await this._promisePlaying();
			await this._promiseEnded();
			this.$element.css('z-index', -1);
			console.log(...log('done catching...'));
		}
		console.groupEnd();
	}


	async pause() {
		return await this.vidElm.pause();
	}
}

class Animation {
	/**@param {jQuery} $animation*/
	constructor($animation) {
		/**@type {jQuery}*/
		this.$element = $animation;
		let svgs = [];
		for (let i of range(0, 51)) {
			if (i == 0) {
				svgs.push(Svg.blackRight);
				continue;
			} else if (i == 51) {
				svgs.push(Svg.base);
				continue;
			}
			let searchElement = i % 7;
			if ([2, 5].includes(searchElement))
				svgs.push(Svg.blackRight);
			else if ([1, 4, 8].includes(searchElement))
				svgs.push(Svg.blackLeft);
			else // [0, 3, 6, 7]
				svgs.push(Svg.blackBoth);
		}
		this.$element.append(svgs);

	}

	/**@param {number} note*/
	_note2svgIndex(note) {
		let scaleStart = int(note / 12) * 7 - 11;
		let indexInScale = note % 12;
		let index = scaleStart + [0, 0, 1, 1, 2, 3, 3, 4, 4, 5, 5, 6][indexInScale];
		if (![0, 2, 4, 5, 7, 9, 11].includes(indexInScale))
			return [index, index + 1];
		return index;

	}

	/**
	 @param {number} note
	 @param {number} noteDuration
	 @param {string} fill
	 * */
	async markKey(note, noteDuration, fill) {
		return new Promise(async resolve => {
			let index = this._note2svgIndex(note);
			let $toMark, baseFill;
			let $children = this.$element.children();
			if (index.length != undefined) { // [3,4] for example
				let [indexLeft, indexRight] = index;
				let $keyLeft = $children.filter(`svg:nth-child(${indexRight})`).children(`.piano-black-left-rect`);
				let $keyRight = $children.filter(`svg:nth-child(${indexLeft})`).children(`.piano-black-right-rect`);
				$toMark = $keyLeft.add($keyRight);
				baseFill = 'black';
			} else { // 3
				$toMark = $children.filter(`svg:nth-child(${index})`).children(`.piano-white-rect`);
				baseFill = 'white';
			}
			$toMark.css({ fill });
			await wait(noteDuration);
			$toMark.css({ fill: baseFill });
			resolve();


		});

	}


}

async function _$messagesInAndOut({ includeSecondary = true, fadeInBigMsg = true }) {
	console.log(...logmod('\t_$messagesInAndOut()'));
	if (fadeInBigMsg)
		await $fadeIn($bigMessage, 700 * F);
	await $fadeIn($smallMessage, 700 * F);
	if (includeSecondary) {
		await wait(700 * F);
		await $fadeIn($smallMessageSecondary, 700 * F);
	}
	await wait(1500 * F);
	let $messages = [$smallMessage];
	if (includeSecondary)
		$messages.push($smallMessageSecondary);
	// if (fadeInBigMsg)
	$messages.push($bigMessage);

	await $fadeOutMany(1500 * F, ...$messages);


}

// Called by showFailed and showPassed
async function _$bigMessageIn() {
	console.log(...logmod('\t_$bigMessageIn()'));
	await concurrent(
		$fadeIn($bigMessage, 700 * F),
		$fadeOutMany(200 * F, $smallMessage, $bigButton, $Sidebar, $pageSubtitle)
	);
	await wait(700 * F);
}

// Called by insidetest.js after awaiting playPreTrialDemo
async function $promptAndMenuIn(numOfNotes) {
	console.log(...logmod('\t$promptAndMenuIn()'));
	$smallMessage.html(`Please play <b>${numOfNotes}</b> notes.`);
	await $fadeInMany(300, $bigButton, $smallMessage, $Sidebar, $pageSubtitle);
}

async function _runVideo(numOfNotes = null) {
	video.setCurrentTimeToFirstOnset();
	await wait(500 * F); // waits until starting frame is updated to new current time
	await $fadeIn(video.$element, 700 * F);
	await video.play(numOfNotes);
	await wait(1500 * F);
	await $fadeOut(video.$element, 700 * F);
}

/**@param {Midi} midi
 * @param {playMidiFileOptions} playOptions*/
async function _runAnimation(midi, playOptions) {
	console.log(...logmod('\t_runAnimation()'));
	await $fadeIn(animation.$element, 700 * F);
	const { playMidiFile } = require("pyano_local_modules/gilad");
	await playMidiFile(playOptions);
	await wait(1500 * F);
	await $fadeOut(animation.$element, 700 * F);
}

/**@param {Midi} midi
 @param {Piano} playbackPiano
 @param {Truth} truth*/
async function playWholeTruthDemo(midi, playbackPiano, truth) {
	console.log(...logmod('playWholeTruthDemo()'));
	$bigMessage.text(`A tutorial`);
	const shouldRunVideo = EStore.isDemoVideo();
	let smallMessage = `Here's ${shouldRunVideo ? "a video" : "an animation"} that shows you what you will be playing.`;
	$smallMessage.html(smallMessage);


	await _$messagesInAndOut({ includeSecondary: false });
	if (shouldRunVideo)
		await _runVideo();
	else
		await _runAnimation(midi, {
			truth,
			animation,
			playbackPiano
		});


}


/**@param {playPreTrialDemoOptions} options
 @return {Promise<void>}*/
async function playPreTrialDemo(options) {
	console.log(...logmod('playPreTrialDemo(options)'));
	let { levelIndex, midi, numOfNotes, playbackPiano, trialIndex, truth } = options;
	const trialNumEng = trialIndex.idx2human(true);
	const shouldRunVideo = EStore.isDemoVideo() && trialIndex == 0 && !EStore.isLearningTempo();
	if (trialIndex == 0)
		$bigMessage.text(`${levelIndex.idx2human(true).title()} level, ${trialNumEng} trial.`);
	else
		$bigMessage.text(`Let's move on to the ${trialNumEng} trial.`);
	$smallMessage.html(`You are going to play <b>${numOfNotes}</b> notes.`);
	$smallMessageSecondary.html(`Here's ${shouldRunVideo ? "a video" : "an animation"} showing only these <b>${numOfNotes}</b> notes.`);

	await _$messagesInAndOut({ includeSecondary: true });


	if (shouldRunVideo) {
		await _runVideo(numOfNotes);

		$smallMessage.html(`You'll now watch the same notes played with an animation.`);
		await _$messagesInAndOut({ fadeInBigMsg: false, includeSecondary: false });
	}
	await _runAnimation(midi, {
		truth,
		animation,
		numOfNotes,
		playbackPiano
	});


}


/**@param {showFailedTrialFeedbackOptions} options
 @return {Promise<void>}*/
async function showFailedTrialFeedback(options) {
	console.log(...logmod('showFailedTrialFeedback(options)'));
	$bigMessage.text("Oi vei!");
	await _$bigMessageIn();
	let { midi, playbackPiano, trialIndex, mistakes, truth } = options;
	const badAccuracyInTempo = bool(mistakes) &&
	                           'note'.in(mistakes) &&
	                           EStore.isLearningTempo();
	if (badAccuracyInTempo) {
		$smallMessage.text(`You got some notes wrong.`);
		$smallMessageSecondary.text("Please try this trial again.");
	} else {
		$smallMessage.text(`You almost got the ${trialIndex.idx2human()} trial right.`);
		$smallMessageSecondary.text("Here's the animation again, highlighting where you got it wrong this time.");
	}
	await _$messagesInAndOut({ fadeInBigMsg: false });


	const numOfNotes = mistakes.length;

	let playOptions = {
		truth,
		animation,
		numOfNotes,
		mistakes,
		playbackPiano,
	};
	if (EStore.isLearningTempo())
		playOptions.speed = 1;
	else
		playOptions.speed = EStore.currentTest().errors_playingspeed;
	await _runAnimation(midi, playOptions);

}

/**@param {Midi} midi
 @param {Piano} playbackPiano
 @param {number} trialIndex
 @param {Truth} truth*/
async function showPassedTrialFeedback(midi, playbackPiano, trialIndex, truth) {
	console.log(...logmod('showPassedTrialFeedback()'));
	$bigMessage.text(`Good job! You passed the ${trialIndex.idx2human()} trial.`);
	await _$bigMessageIn();

	const numOfNotes = EStore.numOfNotesCurrLevel();
	$smallMessage.html(`Let's show you those same <b>${numOfNotes}</b> notes you just got correctly.`);
	$smallMessageSecondary.html(`This will make you even better.`);

	await _$messagesInAndOut({ fadeInBigMsg: false });


	await _runAnimation(midi, {
		truth,
		animation,
		numOfNotes,
		playbackPiano
	});

}

async function showTestCompleteMessages() {
	console.log(...logmod('showTestCompleteMessages()'));
	$bigMessage.text(`Test is over!`);
	$smallMessage.html('You deserve an ice cream. 🍦');

	await $fadeInMany(700, $bigMessage, $smallMessage, $Sidebar);
}


/**@param {TLevel[]} levels
 @param {Truth} truth
 @return {Number[]}*/
function updateLevelTrialSubtitles(levels, truth) {
	let [levelIndex, trialIndex] = EStore.currentTrialCoords();
	$('#page_subtitle > div:nth-child(1) > strong')
		.text(truth.name);
	$('#page_subtitle > div:nth-child(2) > strong')
		.text(`${levelIndex + 1}/${levels.length}`);
	$('#page_subtitle > div:nth-child(3) > strong')
		.text(`${trialIndex + 1}/${levels[levelIndex].trials}`);

	return [levelIndex, trialIndex];
}

let $pageSubtitle = $('<div class="subtitle" id="page_subtitle">')
	.append(
		`<div>Truth file: ${strong(EStore.truthFileName({ withExtension: false }))}</div>`,
		`<div>Level: <strong>1/1</strong></div>`,
		`<div>Trial: <strong>1/1</strong></div>`,
	).hide();
let $bigMessage = $('<div id="big_message">').hide();
let $smallMessage = $('<div class="subtitle" id="small_message">').hide();
let $smallMessageSecondary = $('<div class="subtitle" id="small_message_secondary">')
	.hide();
let $bigButton = $('<button id="big_button">')
	.addClass("my-btn active-btn")
	.append("<span>I'm done playing</span>")
	.hide();
let animation = new Animation($('<div id="animation">').hide());

let video = new Video($(`<video width="1750" preload="auto" id="video">`).hide());


module.exports = {
	$pageSubtitle,
	$bigMessage,
	$smallMessageSecondary,
	$smallMessage,
	$bigButton,
	$promptAndMenuIn,
	playPreTrialDemo,
	showPassedTrialFeedback,
	showFailedTrialFeedback,
	showTestCompleteMessages,
	playWholeTruthDemo,
	updateLevelTrialSubtitles,
	animation,
	video
};
