// **pages/pages.js
let newTestPage = require("./NewTest/newtest.js");
let insideTestPage = require("./InsideTest/insidetest.js");
let recordPage = require("./Record/record.js");
let videoToolsPage = require("./VideoTools/videotools.js");

/**@param {String} page
 @param {Boolean} reload*/
function toPage(page, reload) {
	switch (page) {

		case 'new_test':
			return newTestPage.switch(reload);
		case 'inside_test':
			return insideTestPage.switch();
		case 'record':
			return recordPage.switch(reload);
		case 'video_tools':
			return videoToolsPage.switch(reload);
		default:
			console.error(`pages default, got: ${page}`);
	}
}

module.exports = { newTestPage, insideTestPage, recordPage, videoToolsPage, toPage };
