const Swal = require("sweetalert2");

const smallMixin = Swal.mixin({
	position: "bottom-start",
	timer: 3000,
	toast: true,
	showConfirmButton: false,
	// animation: false,
	customClass: 'animated fadeIn',
});
const Alert = (() => {
	const small = (() => {
		const info = (title, text) =>
			smallMixin.fire({
				title: title,
				text: text,
				type: "info",
			});
		const success = (title, text = null, timer = 4000) =>
			smallMixin.fire({
				title,
				text,
				type: "success",
				timer
			});
		const warning = (title, text = "", onAfterClose = {}) =>
			smallMixin.fire({
				title: title,
				showConfirmButton: true,
				text: text,
				type: "warning",
				onAfterClose: onAfterClose,
			});
		const error = (title, text) =>
			smallMixin.fire({
				title: title,
				text: text,
				type: "error",

			});
		return { info, success, warning, error };
	})();
	const big = (() => {
		const basic = (title, text, onAfterClose = {}, confirmButtonText = "Done") =>
			Swal.fire({
				title: title,
				text: text,
				onAfterClose: onAfterClose,
				preConfirm: onAfterClose,
				allowOutsideClick: false,
				allowEscapeKey: false,
				allowEnterKey: false,
				showConfirmButton: false,
				confirmButtonText: confirmButtonText
			});
		const warning = (title, text) => Swal.fire({
			title: title,
			text: text,
			allowOutsideClick: false,
			allowEscapeKey: false,
			allowEnterKey: false,
			showConfirmButton: false,
		});
		return { basic, warning };
	})();
	return { small, big };
})();

/**@type {Swal}*/
const blockingSwalMixin = Swal.mixin({
	allowEnterKey: false,
	allowEscapeKey: false,
	allowOutsideClick: false,
	showCancelButton: false,
	showCloseButton: false,
	showConfirmButton: false,
	animation: false,

	// backdrop: false,
	customClass: 'animated fadeIn',

});
module.exports = { Alert, blockingSwalMixin, smallMixin };
