// let color = require('./Color';
const color = require('./Color');

const Log = {
	blue: (msg, bold = false) => console.log(`%c${msg}`, `color: ${color.google}; font-weight: ${bold
	                                                                                             ? 900
	                                                                                             : 0}`),

	red: (msg, bold = false) => console.log(`%c${msg}`, `color: ${color.invalid}; font-weight: ${bold
	                                                                                             ? 900
	                                                                                             : 0}`),

	green: (msg, { obj, bold } = {}) => console.log(`%c${msg}`, `color: ${color.valid}; font-weight: ${bold
	                                                                                                   ? 900
	                                                                                                   : 0}`, obj),

	yellow: (msg, { obj, bold } = {}) => console.log(`%c${msg}`, `color: ${color.yellow}; font-weight: ${bold
	                                                                                                     ? 900
	                                                                                                     : 0}`, obj),


	color: (msg, bold = false) => console.log(`%c${msg}`, `color: ${color}; font-weight: ${bold
	                                                                                       ? 900
	                                                                                       : 0}`),
};


module.exports = Log;
