const span = (s, cls = null) => cls
                                ? `<span class="${cls}">${s}</span>`
                                : `<span>${s}</span>`;
const strong = (s) => `<strong>${s}</strong>`;


/**@param {string} html
 @return {jQuery}*/
function $midVertAlign(html) {
	const $ = require("jquery");
	return $('<div class="mid-v-align-container">')
		.append($('<span class="mid-v-align">').html(html));
}


module.exports = {
	span,
	strong,
	$midVertAlign
};
